import {useEffect, useState} from 'react'
import axios from 'axios'

export default function GetData(size, pageNumber) {

    const [loading, setLoading] = useState(true)
    const [error, setError] = useState(false)
    const [pas, setPas] = useState([])
    const [hasMore, setHasMore] = useState(false)

    useEffect(() => {
        setLoading(true)
        setError(false)
        axios({
            method: 'GET',
            url: 'https://api.instantwebtools.net/v1/passenger?page='+String(pageNumber)+'&size='+String(size)
        }).then(res => {
            let a = []
            for(var i=0; i<res.data.data.length;i++){
                a[a.length]._id = res.data.data._id
                a[a.length-1].name = res.data.data.name
                a[a.length-1].airName = res.data.data.airline.name
                a[a.length-1].airId = res.data.data.airline.id
                a[a.length-1].country = res.data.data.airline.country
                a[a.length-1].logo = res.data.data.airline.logo
            }
            console.log(a)
            //setPas(a)
            setHasMore(res.data.data.length > 0)
            setLoading(false)
        }).catch(e => {
            setError(true)
        })
    }, [size, pageNumber])
    return {loading, error, pas, hasMore}
}
