import React, { useState, useRef, useCallback } from 'react'
import GetData from './GetData'

function App() {
  const [pageNumber, setPageNumber] = useState(1)
  const [size, setSize] = useState(7)

  const {
    pas,
    hasMore,
    error,
    loading
  } = GetData(size, pageNumber)

  const observer = useRef()
  const lastElementRef = useCallback(node => {
    if(loading) return
    if(observer.current) observer.current.disconnect()
    observer.current = new IntersectionObserver(entries => {
      if(entries[0].isIntersecting && hasMore){
        setPageNumber(prevPageNumber => prevPageNumber + 1)
        setSize(7)
        console.log('Visible')
      }
    })
    if(node) observer.current.observe(node)
  }, [loading, hasMore])




  return (
    <>
      {pas.map((pass, index) => {
        if (pas.length === index+1){
          return (
            <>
              <div ref={lastElementRef} class="col-sm-6">
                <div class="card">
                  <img class="card-img-top" src={pass.logo} alt="Card image cap"></img>
                  <div class="card-body">
                    <h5>{pass.country}</h5>
                    <br></br>
                    <p>{pass.id}</p>
                    <h4>{pass.name}</h4>


                  </div>
                </div>
              </div>

              <div>{pass.name}</div>
              <div>{pass._id}</div>
              <div>{pass.airName}</div>
              <div>{pass.country}</div>
              <div>{pass.id}</div>
              <div >{pass.logo}</div>
              <br></br>
            </>
          )
        } else {
          return (
            <>
              <div>{pass.name}</div>
              <div>{pass._id}</div>
              <div>{pass.airName}</div>
              <div>{pass.country}</div>
              <div>{pass.id}</div>
              <div>{pass.logo}</div>
              <br></br>
            </>
          )
        }
      })} 
      <div>{loading && 'Loading...'}</div>
      <div>{error && 'Error'}</div>
    </>
  );
}

export default App;
