import React, { useState, useRef, useCallback } from 'react'
import GetData from './GetData'

function App() {
  const [pageNumber, setPageNumber] = useState(1)
  const [size, setSize] = useState(7)
  const observer = useRef()
  const lastElementRef = useCallback(node => {
    console.log(node)
  })

  function handleClick() {
    setPageNumber(pageNumber+1)
    setSize(7)
  }



  const {
    pas,
    hasMore,
    error,
    loading
  } = GetData(size, pageNumber)

  return (
    <>
      {pas.map((pass, index) => {
        console.log(index)
        if (pas.length === index+1){
          return (
            <>
              <div>{pass.name}</div>
              <div>{pass._id}</div>
              <div>{pass.airName}</div>
              <div>{pass.country}</div>
              <div>{pass.id}</div>
              <div>{pass.logo}</div>
              <br></br>
              <br></br>
              <br></br>
              <br  ref={lastElementRef}></br>
            </>
          )
        } else {
          return (
            <>
              <div>{pass.name}</div>
              <div>{pass._id}</div>
              <div>{pass.airName}</div>
              <div>{pass.country}</div>
              <div>{pass.id}</div>
              <div>{pass.logo}</div>
              <br></br>
            </>
          )
        }
      })} 
      <div>{loading && 'Loading...'}</div>
      <div>{error && 'Error'}</div>
      <button type="button" onClick={handleClick}>Click Me!</button>
    </>
  );
}

export default App;
