import React, { useState, useRef, useCallback } from 'react'
import GetData from './GetData'

function App() {
  const [pageNumber, setPageNumber] = useState(1)
  const [size, setSize] = useState(7)
  const observer = useRef()
  const lastElementRef = useCallback(node => {
    console.log(node)
  })

  function handleClick() {
    setPageNumber(pageNumber+1)
    setSize(7)
  }



  const {
    pas,
    hasMore,
    error,
    loading
  } = GetData(size, pageNumber)

  return (
    <>
      {pas.map((pas, index) => {
        if (pas.length === index+1){
          return (
            <>
              <div>{pas.name}</div>
              <div>{pas._id}</div>
              <div>{pas.airName}</div>
              <div>{pas.country}</div>
              <div>{pas.id}</div>
              <div ref={lastElementRef}>{pas.logo}</div>
              <br></br>
            </>
          )
        } else {
          return (
            <>
              <div key={pas.name}>{pas.name}</div>
              <div key={pas._id}>{pas._id}</div>
              <div key={pas.airName}>{pas.airName}</div>
              <div key={pas.country}>{pas.country}</div>
              <div key={pas.id}>{pas.id}</div>
              <div key={pas.logo}>{pas.logo}</div>
              <br></br>
            </>
          )
        }
      })} 
      <div>{loading && 'Loading...'}</div>
      <div>{error && 'Error'}</div>
      <button type="button" onClick={handleClick}>Click Me!</button>
    </>
  );
}

export default App;
