import {useEffect, useState} from 'react'
import axios from 'axios'

export default function GetData(size, pageNumber) {

    const [loading, setLoading] = useState(true)
    const [error, setError] = useState(false)
    const [pas, setPas] = useState([])
    const [hasMore, setHasMore] = useState(false)

    useEffect(() => {
        setLoading(true)
        setError(false)
        axios({
            method: 'GET',
            url: 'https://api.instantwebtools.net/v1/passenger?page='+String(pageNumber)+'&size='+String(size)
        }).then(res => {
            let a = []
            for(var i=0; i<res.data.data.length;i++){
                a = [...a, {"_id":res.data.data[i]._id, "name":res.data.data[i].name, "id":res.data.data[i].airline.id, "airName":res.data.data[i].airline.name, "country":res.data.data[i].airline.country, "logo":res.data.data[i].airline.logo}]
            }
            setPas(prevPas => {
                return [...new Set([...prevPas,...a])]
            })
            setHasMore(res.data.data.length > 0)
            setLoading(false)
        }).catch(e => {
            setError(true)
            console.log(e)
        })
    }, [size, pageNumber])
    console.log(pas)
    return {loading, error, pas, hasMore}
}
