  }*/


  export default function Modl(link) {

    const [show, setShow] = useState(false)
    const [ur, setUr] = useState('')
    const [det, setDet] = useState([])
    const [err, setErr] = useState(false)

    useEffect(() => {
        setUr(link)
        axios({
            method: 'GET',
            url: ur
        }).then(res => {
            setDet(res.data.data)
            setShow(true)
        }).catch(e => {
            setError(true)
            console.log(e)
        })
    }, [size, pageNumber])
    console.log(det)
    return {det, err, show}
}
